package com.thirumal.memory;

/**
 * @author Thirumal
 *
 */
public class MaxMinRange {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Integer minimium value: " + Integer.MIN_VALUE);
		System.out.println("Integer Maximum value: " + Integer.MAX_VALUE);
		//Long
		System.out.println("Long minimium value: " + Long.MIN_VALUE);
		System.out.println("Long Maximum value: " + Long.MAX_VALUE);
		//Comapre
		Long i = 32L;
		Long l = (long) 32;
		System.out.println(i.equals(l));
	}

}
